#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <math.h>
#include "console_colour.h"

void delay_ms(unsigned int i) {
    clock_t start_time = clock();
    while (clock() < start_time + i);
}

static void play_frames(char** frames, unsigned int frame_count, unsigned int frame_time_ms, unsigned int repetitions) {
    for (int j = 0; j < (repetitions + 1); j++) {
        for (int i = 0; i < frame_count; i++) {
            system("cls");
            printf(frames[i]);

            delay_ms(frame_time_ms);
        }
    }

    system("cls");
}

static void play_frames_rainbow(char** frames, unsigned int frame_count, unsigned int frame_time_ms, unsigned int repetitions, float change_rate) {
    int rainbow[] = {
        TEXTCOLOR_RED,
        TEXTCOLOR_LIGHTRED,
        TEXTCOLOR_LIGHTYELLOW,
        TEXTCOLOR_GOLD,
        TEXTCOLOR_LIGHTYELLOW,
        TEXTCOLOR_VIGRANTGREEN,
        TEXTCOLOR_GREEN,
        TEXTCOLOR_LIGHTBLUE,
        TEXTCOLOR_CYAN,
        TEXTCOLOR_BLUE,
        TEXTCOLOR_PURPLE,
        TEXTCOLOR_MAGENTA
    };

    if (change_rate <= 0) change_rate = 1;
    float current_value = 0;
    int rainbow_arr_length = sizeof(rainbow) / sizeof(int);

    for (unsigned int j = 0; j < (repetitions + 1); j++) {
        for (int i = 0; i < frame_count; i++) {
            system("cls");
            int c_index = ((int)roundf(current_value)) % rainbow_arr_length;
            int current = rainbow[c_index];
            current_value = fmodf(current_value + change_rate, rainbow_arr_length);

            printf_rich(frames[i], current);

            delay_ms(frame_time_ms);
        }
    }

    system("cls");

    reset_rich_format();
}

